package com.lehaine.littlekt.graphics.internal

import com.lehaine.littlekt.Context
import com.lehaine.littlekt.file.Base64.decodeFromBase64
import com.lehaine.littlekt.file.atlas.AtlasInfo
import com.lehaine.littlekt.file.atlas.AtlasPage
import com.lehaine.littlekt.file.vfs.readPixmap
import com.lehaine.littlekt.graphics.Texture
import com.lehaine.littlekt.graphics.TextureAtlas
import com.lehaine.littlekt.graphics.TextureSlice
import com.lehaine.littlekt.graphics.font.BitmapFont
import com.lehaine.littlekt.graphics.font.Kerning
import com.lehaine.littlekt.graphics.gl.PixmapTextureData
import com.lehaine.littlekt.math.MutableVec4i
import com.lehaine.littlekt.util.internal.SingletonBase
import kotlinx.serialization.decodeFromString
import kotlin.math.max


private const val defaultFontTxt =
    ""
private const val defaultFontBmp =
    ""
private const val defaultTilesJson =
    ""
private const val defaultTilesBmp =
    ""

/**
 * @author Colton Daily
 * @date 12/29/2021
 */
internal class InternalResources private constructor(private val context: Context) {
    internal companion object : SingletonBase<InternalResources, Context>(::InternalResources)


    lateinit var atlas: TextureAtlas
    lateinit var defaultFont: BitmapFont
    lateinit var white: TextureSlice

    suspend fun load() {
        val page = context.vfs.json.decodeFromString<AtlasPage>(defaultTilesJson.decodeFromBase64().decodeToString())
        val info = AtlasInfo(page.meta, listOf(page))
        val texture = Texture(
            PixmapTextureData(
                defaultTilesBmp.decodeFromBase64().readPixmap(),
                true
            )
        ).also { it.prepare(context) }
        atlas = TextureAtlas(mapOf(info.pages[0].meta.image to texture), info)

        defaultFont = readBitmapFontTxt(
            defaultFontTxt.decodeFromBase64().decodeToString(), mutableMapOf(
                0 to Texture(
                    PixmapTextureData(
                        defaultFontBmp.decodeFromBase64().readPixmap(),
                        true
                    )
                ).also { it.prepare(context) }
            )
        )
        white = atlas.getByPrefix("pixel_white").slice
    }

    private fun readBitmapFontTxt(
        data: String,
        textures: MutableMap<Int, Texture>
    ): BitmapFont {
        val kernings = mutableListOf<Kerning>()
        val glyphs = mutableListOf<BitmapFont.Glyph>()
        var lineHeight = 16f
        var fontSize = 16f
        var base: Float? = null
        var pages = 1
        val lines = data.split("\n")
        val padding = MutableVec4i(0)

        val capChars = charArrayOf(
            'M', 'N', 'B', 'D', 'C', 'E', 'F', 'K', 'A', 'G', 'H', 'I', 'J', 'L', 'O', 'P', 'Q', 'R', 'S',
            'T', 'U', 'V', 'W', 'X', 'Y', 'Z'
        )
        var capHeightFound = false
        var capHeight = 1

        lines.forEach { rline ->
            val line = rline.trim()
            val map = linkedMapOf<String, String>()

            line.split(' ').forEach {
                val (key, value) = it.split('=') + listOf("", "")
                map[key] = value
            }

            when {
                line.startsWith("info") -> {
                    fontSize = map["size"]?.toFloat() ?: 16f
                    map["padding"]?.let {
                        val nums = it.split(',')
                        padding.set(
                            nums[0].toIntOrNull() ?: 0,
                            nums[1].toIntOrNull() ?: 0,
                            nums[2].toIntOrNull() ?: 0,
                            nums[3].toIntOrNull() ?: 0,
                        )
                    }
                }
                line.startsWith("common ") -> {
                    lineHeight = map["lineHeight"]?.toFloatOrNull() ?: 16f
                    base = map["base"]?.toFloatOrNull()
                    pages = map["pages"]?.toIntOrNull() ?: 1
                }
                line.startsWith("char ") -> {
                    val page = map["page"]?.toIntOrNull() ?: 0
                    val texture = textures[page] ?: textures.values.first()
                    val id = map["id"]?.toIntOrNull() ?: 0
                    val width = map["width"]?.toIntOrNull() ?: 0
                    val height = map["height"]?.toIntOrNull() ?: 0

                    if (!capHeightFound) {
                        if (capChars.contains(id.toChar())) {
                            capHeight = height
                            capHeightFound = true
                        } else if (width != 0 && height != 0) {
                            capHeight = max(capHeight, height)
                        }
                    }
                    glyphs += BitmapFont.Glyph(
                        fontSize = fontSize,
                        id = id,
                        slice = TextureSlice(
                            texture,
                            map["x"]?.toIntOrNull() ?: 0,
                            map["y"]?.toIntOrNull() ?: 0,
                            width,
                            height
                        ),
                        xoffset = map["xoffset"]?.toIntOrNull() ?: 0,
                        yoffset = map["yoffset"]?.toIntOrNull() ?: 0,
                        xadvance = map["xadvance"]?.toIntOrNull() ?: 0,
                        page = page
                    )
                }
                line.startsWith("kerning ") -> {
                    kernings += Kerning(
                        first = map["first"]?.toIntOrNull() ?: 0,
                        second = map["second"]?.toIntOrNull() ?: 0,
                        amount = map["amount"]?.toIntOrNull() ?: 0
                    )
                }
            }
        }

        capHeight -= padding.x + padding.z

        return BitmapFont(
            fontSize = fontSize,
            lineHeight = lineHeight,
            base = base ?: lineHeight,
            capHeight = capHeight.toFloat(),
            textures = textures.values.toList(),
            glyphs = glyphs.associateBy { it.id },
            kernings = kernings.associateBy { Kerning.buildKey(it.first, it.second) },
            pages = pages
        )
    }
}
